from aiogram.filters import Text
from aiogram.fsm.context import FSMContext
from aiogram.fsm.state import any_state
from aiogram.types import InlineKeyboardMarkup, InlineKeyboardButton, Message, CallbackQuery
from aiogram.utils.keyboard import InlineKeyboardBuilder
from aiogram.utils.markdown import hitalic

from db.answers import Answers
from handlers.user_handlers import start_LLIC
from settings import InputMessage, sticker_ids
from db.users_stat import Users_stat
from aiogram import types, F, Router, Bot

from utils.is_now_day import is_now_day

day_router20 = Router()


@day_router20.callback_query(Text(text="confirm|20"), any_state)
@is_now_day(20)
async def start_day20(message: CallbackQuery, state: FSMContext, bot: Bot):
    if int(await Users_stat(message.from_user.id).get_user_day()) == int(message.data.split("|")[1]):
        await state.clear()
        await message.message.answer("–£–ø—Ä–∞–≤–ª–µ–Ω–∏–µ —Å–æ–±—Å—Ç–≤–µ–Ω–Ω—ã–º–∏ —ç–º–æ—Ü–∏—è–º–∏ ‚Äî –æ–¥–∏–Ω –∏–∑ –≤–∞–∂–Ω–µ–π—à–∏—Ö –∂–∏–∑–Ω–µ–Ω–Ω—ã—Ö –Ω–∞–≤—ã–∫–æ–≤, "
                                      "–Ω–æ –¥–∞–ª–µ–∫–æ –Ω–µ –≤—Å–µ –ø–æ–Ω–∏–º–∞—é—Ç, —á—Ç–æ –æ–Ω –≤–∫–ª—é—á–∞–µ—Ç –≤ —Å–µ–±—è –Ω–µ —Ç–æ–ª—å–∫–æ –ø–æ–¥–∞–≤–ª–µ–Ω–∏–µ –∏–ª–∏ —Å–æ–∫—Ä—ã—Ç–∏–µ —á—É–≤—Å—Ç–≤. "
                                      "–í—Å–µ –º—ã –æ–∫–∞–∑—ã–≤–∞–µ–º—Å—è –≤ —Å–∏—Ç—É–∞—Ü–∏—è—Ö, –∫–æ–≥–¥–∞ –Ω–æ—Ä–º—ã –∏ –ø—Ä–∞–≤–∏–ª–∞ –¥–∏–∫—Ç—É—é—Ç, –∫–∞–∫ –º—ã –¥–æ–ª–∂–Ω—ã —Å–µ–±—è –≤–µ—Å—Ç–∏, "
                                      "–∏ —á–∞—Å—Ç–æ –æ–Ω–∏ –ø–æ–¥—Ä–∞–∑—É–º–µ–≤–∞—é—Ç —Å–¥–µ—Ä–∂–∏–≤–∞–Ω–∏–µ —è—Ä–∫–∏—Ö —ç–º–æ—Ü–∏–π, –æ—Å–æ–±–µ–Ω–Ω–æ –Ω–µ–≥–∞—Ç–∏–≤–Ω—ã—Ö. "
                                      "–û–¥–Ω–∞–∫–æ –µ—Å–ª–∏ –Ω–µ –ø–æ–∑–≤–æ–ª—è—Ç—å —ç–º–æ—Ü–∏—è–º –≤—ã—Ö–æ–¥–∏—Ç—å –Ω–∞—Ä—É–∂—É, –æ–Ω–∏ –±—É–¥—É—Ç –Ω–∞–∫–∞–ø–ª–∏–≤–∞—Ç—å—Å—è –∏ –æ–¥–Ω–∞–∂–¥—ã –≤—Å–ø–ª—ã–≤—É—Ç, "
                                      "–∏–ª–∏ –º–æ–≥—É—Ç –ø—Ä–æ—è–≤–∏—Ç—å —Å–µ–±—è –≤ –≤–∏–¥–µ –±–æ–ª–µ–∑–Ω–µ–π. –ü–æ—ç—Ç–æ–º—É –∫—Ä–∞–π–Ω–µ –≤–∞–∂–Ω–æ –¥–∞–≤–∞—Ç—å —ç–º–æ—Ü–∏—è–º ‚Äú–≤—Ä–µ–º—è –∏ –º–µ—Å—Ç–æ‚Äù. "
                                      "–í–æ–∑–º–æ–∂–Ω–æ –Ω–µ —Ç–∞–º, –≥–¥–µ –æ–Ω–∏ –Ω–∞—Å—Ç–∏–≥–Ω—É—Ç. –í–æ–∑–º–æ–∂–Ω–æ, –≤–µ—á–µ—Ä–æ–º –¥–æ–º–∞. –í–æ–∑–º–æ–∂–Ω–æ, —á–µ—Ä–µ–∑ –ø–∞—Ä—É –¥–Ω–µ–π. –ß—Ç–æ –∂–µ —Å –Ω–∏–º–∏ –≤ –∏—Ç–æ–≥–µ –¥–µ–ª–∞—Ç—å?")
        await message.message.answer("–ü–µ—Ä–≤—ã–º —à–∞–≥–æ–º —ç–º–æ—Ü–∏—é –Ω–∞–¥–æ –∑–∞–º–µ—Ç–∏—Ç—å. –≠—Ç–æ –±—ã–≤–∞–µ—Ç –Ω–µ —Ç–∞–∫-—Ç–æ –ø—Ä–æ—Å—Ç–æ. –í –Ω–∞—Å –≤–∏—Ö—Ä–µ–º –ø—Ä–æ–Ω–æ—Å–∏—Ç—Å—è –º–Ω–æ–∂–µ—Å—Ç–≤–æ —ç–º–æ—Ü–∏–π –∏ –≤ —ç—Ç–æ–π —Å–º–µ—Å–∏ –±—ã–≤–∞–µ—Ç —Å–ª–æ–∂–Ω–æ —á—Ç–æ-—Ç–æ —Ä–∞—Å–ø–æ–∑–Ω–∞—Ç—å. –ò–Ω–æ–≥–¥–∞ –ø—Ä–æ—Å—Ç–æ –Ω–µ —Ö–æ—á–µ—Ç—Å—è. –ù–æ –≤—Å—ë-—Ç–∞–∫–∏ —Å–º–µ—Å—å –Ω–∞–¥–æ —Ä–∞–∑–±–∏—Ç—å –ø–æ –∏–Ω–≥—Ä–µ–¥–∏–µ–Ω—Ç–∞–º, –∞ –∏–Ω–≥—Ä–µ–¥–∏–µ–Ω—Ç—ã –Ω–∞–∑–≤–∞—Ç—å (—á—Ç–æ —Ç—ã —Ç—Ä–µ–Ω–∏—Ä–æ–≤–∞–ª—Å—è –¥–µ–ª–∞—Ç—å –≤ –ø—Ä–æ—à–ª–æ–π —á–∞—Å—Ç–∏ –ø—Ä–æ–≥—Ä–∞–º–º—ã)\n–ü–æ—Å–ª–µ —Ç–æ–≥–æ, –∫–∞–∫ —Ç—ã —Å–º–æ–≥ –≤—ã–¥–µ–ª–∏—Ç—å –∏ –Ω–∞–∑–≤–∞—Ç—å —ç–º–æ—Ü–∏—é, –µ—ë –Ω–∞–¥–æ –ø—Ä–∏–Ω—è—Ç—å. –ù–µ –≥–Ω–∞—Ç—å –æ—Ç —Å–µ–±—è, –Ω–µ –¥—É–º–∞—Ç—å –æ —Ç–æ–º, —á—Ç–æ —Ç—ã –Ω–µ –¥–æ–ª–∂–µ–Ω –∏–ª–∏ –Ω–µ —Ö–æ—á–µ—à—å –µ—ë —á—É–≤—Å—Ç–≤–æ–≤–∞—Ç—å, –∞ –ø—Ä–∏–Ω—è—Ç—å. –ü—Ä–∏–Ω—è—Ç—å, —á—Ç–æ –æ–Ω–∞ –ø–æ—è–≤–∏–ª–∞—Å—å. –ü—Ä–∏–Ω—è—Ç—å, —á—Ç–æ –æ–Ω–∞ –æ —á—ë–º-—Ç–æ –≥–æ–≤–æ—Ä–∏—Ç. –ù–µ –±—ã–≤–∞–µ—Ç –ø–ª–æ—Ö–∏—Ö –∏ —Ö–æ—Ä–æ—à–∏—Ö —ç–º–æ—Ü–∏–π. –≠–º–æ—Ü–∏–∏ ‚Äî –∏–Ω–¥–∏–∫–∞—Ç–æ—Ä—ã –æ—Ç–Ω–æ—à–µ–Ω–∏—è –∫ —Ç–æ–º—É, —á—Ç–æ –ø—Ä–æ–∏—Å—Ö–æ–¥–∏—Ç, –∏ –±–µ–∑ –Ω–∏—Ö, –Ω–∞–º –±—ã–ª–æ –±—ã –æ—á–µ–Ω—å —Å–ª–æ–∂–Ω–æ –æ—Ä–∏–µ–Ω—Ç–∏—Ä–æ–≤–∞—Ç—å—Å—è –≤ –º–∏—Ä–µ")
        keyboard = InlineKeyboardBuilder().row(
            InlineKeyboardButton(text="–ü–æ–Ω—è–ª) –ë—É–¥—É –ø—Ä–∏—Å–ª—É—à–∏–≤–∞—Ç—å—Å—è –∫ —Å–µ–±–µ", callback_data="Understood|20")
        )
        await message.message.answer("–ö–æ–≥–¥–∞ —É —Ç–µ–±—è –ø–æ–ª—É—á–∏–ª–æ—Å—å –ø—Ä–∏–Ω—è—Ç—å —ç–º–æ—Ü–∏—é, –ø—Ä–æ–∂–∏–≤–∏ –µ—ë. "
                             "–ü–æ—Å–º–æ—Ç—Ä–∏ –≤–Ω—É—Ç—Ä—å —Å–µ–±—è –∏ –ø–æ—á—É–≤—Å—Ç–≤—É–π, —á—Ç–æ —Ç–µ–±–µ —Ö–æ—á–µ—Ç—Å—è —Å–¥–µ–ª–∞—Ç—å: "
                             "–≤–æ–∑–º–æ–∂–Ω–æ, —Ç–µ–±–µ –∑–∞—Ö–æ—á–µ—Ç—Å—è –ø–æ–¥–µ–ª–∏—Ç—å—Å—è —Å –∫–µ–º-—Ç–æ –∏ –ø–æ–ª—É—á–∏—Ç—å –ø–æ–¥–¥–µ—Ä–∂–∫—É (—Ç—É—Ç –±—É–¥–µ—Ç –∑–¥–æ—Ä–æ–≤–æ, "
                             "–µ—Å–ª–∏ —Ç—ã —Å–º–æ–∂–µ—à—å –ø–æ–Ω—è—Ç—å, –∫–∞–∫–∞—è –∏–º–µ–Ω–Ω–æ –ø–æ–¥–¥–µ—Ä–∂–∫–∞ —Ç–µ–±–µ –Ω—É–∂–Ω–∞ –∏ —Å–∫–∞–∂–µ—à—å –æ–± —ç—Ç–æ–º), "
                             "–≤–æ–∑–º–æ–∂–Ω–æ, —Ç–µ–±–µ –∑–∞—Ö–æ—á–µ—Ç—Å—è –æ—Å—Ç–∞—Ç—å—Å—è –æ–¥–Ω–æ–º—É –∏ –ø–æ—Å–ª—É—à–∞—Ç—å –æ–ø—Ä–µ–¥–µ–ª–µ–Ω–Ω—É—é –º—É–∑—ã–∫—É, "
                             "–≤–æ–∑–º–æ–∂–Ω–æ, —Ç–µ–±–µ –∑–∞—Ö–æ—á–µ—Ç—Å—è –ø–æ–π—Ç–∏ –≤ –∑–∞–ª –∏ –ø–æ–±–∏—Ç—å –≥—Ä—É—à—É/–ø–æ–±–µ–≥–∞—Ç—å/–ø–æ—Ç–∞–Ω—Ü–µ–≤–∞—Ç—å ‚Äî –≤ –∞–∫—Ç–∏–≤–Ω–æ—Å—Ç–∏ —Ç–æ–∂–µ –æ—Ç–ª–∏—á–Ω–æ –ø—Ä–æ–∂–∏–≤–∞—é—Ç—Å—è —ç–º–æ—Ü–∏–∏. "
                             "–ì–ª–∞–≤–Ω–æ–µ, –¥–∞—Ç—å —ç–º–æ—Ü–∏—è–º –≤—Ä–µ–º—è –∏ –º–µ—Å—Ç–æ üëê", reply_markup=keyboard.as_markup())


@day_router20.callback_query(Text(text="Understood|20"))
@is_now_day(20)
async def answer_day20_1(message: CallbackQuery, state: FSMContext, bot: Bot):
    question = await message.message.answer("–ò—Ç–∞–∫, –º—ã –æ–±—Å—É–¥–∏–ª–∏ —Ñ–∏–∑–∏–æ–ª–æ–≥–∏—á–µ—Å–∫–∏–π —É—Ä–æ–≤–µ–Ω—å, –ø—Å–∏—Ö–æ–ª–æ–≥–∏—á–µ—Å–∫–∏–π, —Å –µ–≥–æ –ø–µ—Ä—Ü–µ–ø—Ç–∏–≤–Ω—ã–º –∏ —Ä–µ—Ñ–ª–µ–∫—Å–∏–≤–Ω—ã–º–∏ –∫–æ–º–ø–æ–Ω–µ–Ω—Ç–∞–º–∏, –∏ —É –Ω–∞—Å –æ—Å—Ç–∞–ª—Å—è –ø–æ–≤–µ–¥–µ–Ω—á–µ—Å–∫–∏–π. "
                         "–ö–∞–∫ –¥—É–º–∞–µ—à—å, —á—Ç–æ —Ç—É–¥–∞ –≤—Ö–æ–¥–∏—Ç?")
    await state.set_state(InputMessage.input_answer_state20_3)
    await state.update_data(question=str(await Users_stat(message.from_user.id).get_user_day()) + ". " + question.text)


@day_router20.message(F.text, InputMessage.input_answer_state20_3)
@is_now_day(20)
async def answer_day19_1(message: types.Message, state: FSMContext, bot: Bot):
    data = await state.get_data()
    question = data.get("question")
    answers = Answers()
    await answers.add_answer(question=question, answer=message.text, user_id=message.from_user.id)
    await state.clear()
    keyboard = InlineKeyboardBuilder()
    await message.answer_sticker(sticker=sticker_ids[5])
    keyboard.row(InlineKeyboardButton(text="–Ø —Ç–∞–∫ –æ–±—ã—á–Ω–æ –∏ –¥–µ–ª–∞—é", callback_data="OKAY_LETGO|20"))
    keyboard.row(InlineKeyboardButton(text="–í–æ–∑—å–º—É –Ω–∞ –∑–∞–º–µ—Ç–∫—É", callback_data="OKAY_LETGO|20"))
    keyboard.row(InlineKeyboardButton(text="–ë—É–¥—É –ø—Ä–æ–±–æ–≤–∞—Ç—å",
                                      callback_data="OKAY_LETGO|20"))
    await message.answer(
        "–ß—Ç–æ –∂–µ –º–æ–∂–Ω–æ —Å–¥–µ–ª–∞—Ç—å —Å–æ —Å—Ç—Ä–µ—Å—Å–æ–º –Ω–∞ –ø–æ–≤–µ–¥–µ–Ω—á–µ—Å–∫–æ–º —É—Ä–æ–≤–Ω–µ? –ö–æ–Ω–µ—á–Ω–æ, –Ω–µ–ø–æ—Å—Ä–µ–¥—Å—Ç–≤–µ–Ω–Ω–æ —Ä–µ—à–∏—Ç—å –ø—Ä–æ–±–ª–µ–º—É:\n"
        "1. –û–ø—Ä–µ–¥–µ–ª–∏ –ø—Ä–æ–±–ª–µ–º—É: –æ–ø–∏—à–∏ –µ—ë —á–µ—Ç–∫–æ –∏ –∫–æ–Ω–∫—Ä–µ—Ç–Ω–æ, –≤—ã–¥–µ–ª–∏ –æ—Å–Ω–æ–≤–Ω—ã–µ –∞—Å–ø–µ–∫—Ç—ã, –∫–æ—Ç–æ—Ä—ã–µ –≤—ã–∑—ã–≤–∞—é—Ç –±–µ—Å–ø–æ–∫–æ–π—Å—Ç–≤–æ.\n"
        "2. –ü—Ä–æ–∞–Ω–∞–ª–∏–∑–∏—Ä—É–π –ø—Ä–∏—á–∏–Ω—ã: –≤—ã–¥–µ–ª–∏ –æ—Å–Ω–æ–≤–Ω—ã–º–∏ –ø—Ä–∏—á–∏–Ω—ã –∏ —á—Ç–æ –ø–æ—Å–ª—É–∂–∏–ª–æ –∏—Ö –≤–æ–∑–Ω–∏–∫–Ω–æ–≤–µ–Ω–∏—é (—Ç—É—Ç –º–æ–∂–µ—Ç –æ—Ç–ª–∏—á–Ω–æ –ø–æ–º–æ—á—å –º–µ—Ç–æ–¥ ‚Äú5 –ø–æ—á–µ–º—É‚Äù: "
        "—Ç—ã –∑–∞–¥–∞–µ—à—å—Å—è –≤–æ–ø—Ä–æ—Å–æ–º, –ø–æ—á–µ–º—É –ø—Ä–æ–∏–∑–æ—à–ª–æ —Ç–æ –∏–ª–∏ –∏–Ω–æ–µ, –Ω–∞—Ö–æ–¥–∏—à—å –æ—Ç–≤–µ—Ç –∏ –¥–∞–ª–µ–µ –∑–∞–¥–∞–µ—à—å—Å—è –≤–æ–ø—Ä–æ—Å–æ–º, –∞ —á—Ç–æ —Å—Ç–∞–ª–æ –ø—Ä–∏—á–∏–Ω–æ–π —ç—Ç–æ–π –ø—Ä–∏—á–∏–Ω—ã, "
        "–∏ —Ç–∞–∫ –ø—Ä–æ–¥–æ–ª–∂–∞–µ—à—å –∑–∞–¥–∞–≤–∞—Ç—å—Å—è –≤–æ–ø—Ä–æ—Å–æ–º ‚Äú–ø–æ—á–µ–º—É?‚Äù, –ø–æ–∫–∞ –Ω–µ –¥–æ–±–µ—Ä–µ—à—å—Å—è –¥–æ –ø–µ—Ä–≤–æ–∏—Å—Ç–æ—á–Ω–∏–∫–∞ –ø—Ä–æ–±–ª–µ–º—ã).\n"
        "3. –ü–æ—Å—Ç–∞–≤—å —Ü–µ–ª—å: –æ–ø—Ä–µ–¥–µ–ª–∏, –∫–∞–∫ –¥–æ–ª–∂–µ–Ω –≤—ã–≥–ª—è–¥–µ—Ç—å —Ä–µ–∑—É–ª—å—Ç–∞—Ç.\n"
        "4. –û—Ü–µ–Ω–∏ —Ç—Ä–µ–±—É–µ–º—ã–µ –∏ –∏–º–µ—é—â–∏–µ—Å—è —Ä–µ—Å—É—Ä—Å—ã: –≤—Ä–µ–º—è, —Å–∏–ª—ã, –∑–Ω–∞–Ω–∏—è, —Ñ–∏–Ω–∞–Ω—Å—ã, —Å–≤—è–∑–∏, –∏–Ω—Å—Ç—Ä—É–º–µ–Ω—Ç—ã –∏ —Ç.–ø.\n"
        "5. –†–∞–∑—Ä–∞–±–æ—Ç–∞–π –ø–ª–∞–Ω –¥–µ–π—Å—Ç–≤–∏–π: —Ä–∞–∑–¥–µ–ª–∏ —Ü–µ–ª—å –Ω–∞ –ø–æ–¥—Ü–µ–ª–∏, –ø–æ–¥—Ü–µ–ª–∏ ‚Äî –Ω–∞ –∑–∞–¥–∞—á–∏, –∞ –∑–∞–¥–∞—á–∏ ‚Äî –Ω–∞ –∫–æ–Ω–∫—Ä–µ—Ç–Ω—ã–µ —à–∞–≥–∏. "
        "–ß–µ–º –±–æ–ª–µ–µ –æ–ø—Ä–µ–¥–µ–ª–µ–Ω–Ω—ã–º –±—É–¥–µ—Ç —Ç–≤–æ–π –ø–ª–∞–Ω, —Ç–µ–º –ª–µ–≥—á–µ —Ç–µ–±–µ –±—É–¥–µ—Ç –ø—Ä–∏—Å—Ç—É–ø–∏—Ç—å –∏ –ø—Ä–æ–¥–æ–ª–∂–∞—Ç—å –∏–¥—Ç–∏ –∫ —Ü–µ–ª–∏.\n"
        "6. –†–µ–∞–ª–∏–∑—É–π –ø–ª–∞–Ω: –Ω–∞—á–Ω–∏ —à–∞–≥ –∑–∞ —à–∞–≥–æ–º –≤—ã–ø–æ–ª–Ω—è—Ç—å –ø–ª–∞–Ω, –æ—Ü–µ–Ω–∏–≤–∞—è –ø—Ä–æ–≥—Ä–µ—Å—Å –∏ –≤–Ω–æ—Å—è –∫–æ—Ä—Ä–µ–∫—Ç–∏—Ä–æ–≤–∫–∏ –ø—Ä–∏ –Ω–µ–æ–±—Ö–æ–¥–∏–º–æ—Å—Ç–∏.\n"
        "7. –û—Ü–µ–Ω–∏ —Ä–µ–∑—É–ª—å—Ç–∞—Ç: –ø–æ—Å–º–æ—Ç—Ä–∏, –ø—Ä–∏—à—ë–ª –ª–∏ —Ç—ã –∫ —Ç–æ–π —Ü–µ–ª–∏, –∫–æ—Ç–æ—Ä—É—é —Å—Ç–∞–≤–∏–ª (–∏–ª–∏ –∫ –∫–∞–∫–æ–π-—Ç–æ –¥—Ä—É–≥–æ–π), "
        "–ø—Ä–æ–∞–Ω–∞–ª–∏–∑–∏—Ä—É–π, —á—Ç–æ —Å—Ä–∞–±–æ—Ç–∞–ª–æ –∏ —á—Ç–æ –º–æ–∂–Ω–æ —É–ª—É—á—à–∏—Ç—å –≤ –±—É–¥—É—â–µ–º.\n"
        "8. –†–∞—Å—Å–º–æ—Ç—Ä–∏ –∏ –ø—Ä–µ–¥–ø—Ä–∏–º–∏ –≤–æ–∑–º–æ–∂–Ω—ã–µ —à–∞–≥–∏ –¥–ª—è –ø—Ä–µ–¥–æ—Ç–≤—Ä–∞—â–µ–Ω–∏—è –ø–æ–≤—Ç–æ—Ä–Ω–æ–≥–æ –≤–æ–∑–Ω–∏–∫–Ω–æ–≤–µ–Ω–∏—è –ø–æ–¥–æ–±–Ω–æ–π –ø—Ä–æ–±–ª–µ–º—ãüòâ")
    await message.answer("–ü–æ–ø—Ä–æ–±—É–µ—à—å?", reply_markup=keyboard.as_markup())


@day_router20.callback_query(Text(text="OKAY_LETGO|20"))
@is_now_day(20)
async def answer_day20_1(message: CallbackQuery, state: FSMContext, bot: Bot):
    await message.message.answer_sticker(sticker=sticker_ids[7])
    await message.message.answer("–£—Ä–∞! –ú—ã –ø—Ä–æ—à–ª–∏—Å—å –ø–æ –≤—Å–µ–º —É—Ä–æ–≤–Ω—è–º –ø—Ä–æ—è–≤–ª–µ–Ω–∏—è —Å—Ç—Ä–µ—Å—Å–∞ –∏ –∑–∞–≤—Ç—Ä–∞ –≤—Å—Ç—É–ø–∏–º –Ω–∞ —Ñ–∏–Ω–∏—à–Ω—É—é –ø—Ä—è–º—É—éü•≥\n\n"
                         "–î–∞–≤–∞–π –µ—â—ë –±—ã—Å—Ç—Ä–µ–Ω—å–∫–æ –ø–æ—Å–º–æ—Ç—Ä–∏–º, —á—Ç–æ —É —Ç–µ–±—è —Å —Å–æ—Å—Ç–æ—è–Ω–∏–µ–º")
    await start_LLIC(message, state, bot)
